import { Component} from 'react'
import { Card,Col, Container, Row, Badge } from 'react-bootstrap'
import items from '../data/horror.json'
import CommentArea from './CommentArea'


class NewRelease extends Component{

    state = {
       selectedBook : false

    }
    render(){
        return(
            <Container fluid> 
            <h2>Books Available</h2>
                <Row>
                {
                    items.map(item =>(
                        <Col xs={12} md={4} lg={3}>
                        <Card style={{ height: '416px'}} className="mt-3" key={item.asin}>
                            <Card.Img style={{ height: '300px'}} variant="top" src={item.img} 
                            onClick={
                                ()=>{
                                    this.setState({
                                        selectedBook: true
                                    })
                                }
                            }/>
                            <Card.Body>
                                <Card.Title>{item.title} <Badge variant="warning">â‚¬ {item.price}</Badge> 
                                </Card.Title>
                           </Card.Body>
                           {this.state.selectedBook}
                         <CommentArea bookData={item}/>
                        </Card>


                        </Col>
                            ))
                        }

                </Row>

            </Container>

        )
    }

}
export default NewRelease
