import { Component} from 'react'
import { Card, ListGroup,ListGroupItem,Col, Container, Row, Badge,Figure,Form,Button } from 'react-bootstrap'
import items from '../data/horror.json'
import CommentArea from './component/CommentList'

class NewRelease extends Component{

    state = {
        selectedBook:null

    }
    render(){
        return(
            <Container fluid> 
            <h2>Books Available</h2>
                <Row>
                {
                    items.map(item =>(
                        <Col xs={12} md={4} lg={3}>
                        <Card style={{ height: '416px'}} className="mt-3" key={item.asin}>
                            <Card.Img style={{ height: '300px'}} variant="top" src={item.img} 
                            onClick={
                                ()=>{
                                    this.setState({
                                        selectedBook: item
                                    })
                                }
                            }/>
                            <Card.Body>
                                <Card.Title>{item.title} <Badge variant="warning">â‚¬ {item.price}</Badge> 
                                </Card.Title>
                           </Card.Body>
                           <CommentArea />
                        </Card>
                        <Figure>
                                <Figure.Image
                                    width={171}
                                    height={180}
                                    alt="171x180"
                                    src={item.img}
                                />
                                <Figure.Caption>
                                    {item.comment}
                                </Figure.Caption>
                                <Form>
                                    <Form.Group className="mb-3">

                                        <Form.Control id="disabledTextInput" placeholder="Write Your Comment" />
                                        <Button type="submit">Save</Button>
                                    </Form.Group>
                                        </Form>
                              </Figure>
                        </Col>
                            ))
                        }

                </Row>
            </Container>

        )
    }
}
export default NewRelease
