                <Row>
                {
                    items.map(item =>(
                        <Col xs={12} md={4} lg={3}>
                        <Card style={{ width: '18rem' }} className="mt-3" key={item.asin}>
                            <Card.Img variant="top" src={item.img} />
                            <Card.Body>
                                <Card.Title>{item.title}</Card.Title>
                                <div><Badge bg="warning" text="dark">â‚¬ {item.price}</Badge></div>
                           </Card.Body>
                        </Card>
                        </Col>
                            ))
                        }

                </Row>
            </Container>

        )
    }
}
export default NewRelease
