import {Component} from 'react'
import { Figure,Form,Button } from 'react-bootstrap'


class CommentArea extends Component {
    state = {
        comment: ''
    }

    viewComment = async () => {
      try {

        let response = await fetch('https://striveschool-api.herokuapp.com/api/comments/',{
            headers:{
                'Content-Type': 'application/json',
                'Authorization': "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MGRjNzMzNGIzNTgxNzAwMTVjMjI3NWIiLCJpYXQiOjE2MjU3NDkxNzcsImV4cCI6MTYyNjk1ODc3N30.H4RZbFQaaj7QdwXtGHKnEQINp6zmhd54Eqt-fM0b15E"
            }
        })
        if (response.ok){
            let comment = await response.json()

        }
        console.log(comment)

      } catch (error) {
          console.log(error)
      }
    }

    render(){
        return(
            <Figure>
                    <Figure.Image
                        width={171}
                        height={180}
                        alt="171x180"
                        src="holder.js/171x180"
                    />
                    <Figure.Caption>
                        Nulla vitae elit libero, a pharetra augue mollis interdum.
                    </Figure.Caption>
                    <Form>
                        <Form.Group className="mb-3">

                            <Form.Control id="disabledTextInput" placeholder="Write Your Comment" />
                            <Button type="submit">Save</Button>
                        </Form.Group>
                    </Form>
            </Figure>
        )
    }

}
export default CommentArea
