import { Card,Col, Container, Row, Badge } from 'react-bootstrap'
import items from '../data/horror.json'


class NewRelease extends Component{

    state = {
       comments: []

    }
    render(){
        return(
            <Container fluid> 
            <h2>Books Available</h2>
                <Row>
                {
                    items.map(item =>(
                        <Col xs={12} md={4} lg={3}>
                        <Card style={{ height: '416px'}} className="mt-3" key={item.asin}>
                            <Card.Img style={{ height: '300px'}} variant="top" src={item.img} 
                            onClick={
                                ()=>{
                                    this.setState({
                                        selectedBook: item
                                    })
                                }
                            }/>
                            <Card.Body>
                                <Card.Title>{item.title} <Badge variant="warning">â‚¬ {item.price}</Badge> 
                                </Card.Title>
                           </Card.Body>

                        </Card>

                        </Col>
                            ))
                        }

                </Row>
            </Container>

        )
    }
    componentDidMount = async()=>{
        const response = await fetch(' https://striveschool-api.herokuapp.com/api/comments/' + this.props.bookInfo.asin,{
            headers:{
                'Authorization': "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MGRjNzMzNGIzNTgxNzAwMTVjMjI3NWIiLCJpYXQiOjE2MjU3NDkxNzcsImV4cCI6MTYyNjk1ODc3N30.H4RZbFQaaj7QdwXtGHKnEQINp6zmhd54Eqt-fM0b15E"
            }
        })
        const comments = await response.json()
        console.log(comments)
    }
}
export default NewRelease
